  __name: 'LeftMenu',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const apiUrl = API_BASE_URL;
    const paginatedProducts = ref([]);
    const totalProducts = computed(() => paginatedProducts.value.length);
    const __returned__ = {
      apiUrl,
      paginatedProducts,
      totalProducts,
      ProductCart,
      ProductPagination,
      ProductFilter,
      ref,
      computed,
      get API_BASE_URL() {
        return API_BASE_URL;
      }
    };
