import { computed, ref } from 'vue';

// Определяем пропсы без деструктуризации
const productsPerPage = 10;

// Используем props.totalProducts, чтобы вычислить общее количество страниц

export default {
  __name: 'ProductPagination',
  props: ['totalProducts'],
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;
    const currentPage = ref(1);
    const totalPages = computed(() => Math.ceil(props.totalProducts / productsPerPage));
    const nextPage = () => {
      if (currentPage.value < totalPages.value) currentPage.value++;
    };
    const prevPage = () => {
      if (currentPage.value > 1) currentPage.value--;
    };
    const __returned__ = {
      props,
      currentPage,
      productsPerPage,
      totalPages,
      nextPage,
      prevPage,
      computed,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
