import { ref, onMounted, computed } from 'vue';
import ProductCart from '../Products/ProductCart.vue';
import { API_BASE_URL } from '@/config';

// Храним товары и базовый URL для API

export default {
  __name: 'DiscountP50',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const products = ref([]);
    const apiUrl = API_BASE_URL;

    // Храним продукты со скидкой 50% и более
    const discountedProducts = computed(() => {
      return products.value.filter(product => {
        if (product.discount_price && product.price) {
          const discount = (product.price - product.discount_price) / product.price * 100;
          return discount >= 20; // Скидка 50% или более
        }
        return false; // Нет скидки или цена не указана
      });
    });
    const fetchProducts = async () => {
      try {
        const response = await fetch(`${apiUrl}/api/products`);
        if (!response.ok) throw new Error('Ошибка при получении данных');
        const data = await response.json();
        products.value = data;
      } catch (error) {
        console.error('Ошибка:', error);
      }
    };
    onMounted(fetchProducts);
    const __returned__ = {
      products,
      apiUrl,
      discountedProducts,
      fetchProducts,
      ref,
      onMounted,
      computed,
      ProductCart,
      get API_BASE_URL() {
        return API_BASE_URL;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
