import { ref, onMounted, computed } from 'vue';
export default {
  __name: 'LeftMenu',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const categories = ref([]);
    const products = ref([]);
    const brands = ref([]);
    const selectedBrand = ref(null); // Для одного выбранного бренда

    const selectedCharacteristics = ref({}); // Для хранения выбранных характеристик
    const characteristics = ref({}); // Уникальные характеристики

    const selectedBrands = ref([]);
    const handleBrandChange = brand => {
      // Если бренд уже выбран, оставляем его
      if (selectedBrands.value.includes(brand)) {
        selectedBrands.value = [brand]; // Оставляем только выбранный бренд
      }
    };
    onMounted(async () => {
      await loadCategories();
      await loadProducts();
    });

    // Функция для загрузки категорий
    const loadCategories = async () => {
      try {
        const response = await fetch('http://localhost:1452/api/products');
        if (!response.ok) {
          throw new Error('Ошибка при загрузке категорий');
        }
        const data = await response.json();
        categories.value = data;
        extractBrands(data);
        extractCharacteristics(data);
      } catch (error) {
        console.error('Ошибка при загрузке категорий:', error);
      }
    };

    // Функция для загрузки товаров
    const loadProducts = async () => {
      try {
        const response = await fetch('http://localhost:1452/api/products');
        if (!response.ok) {
          throw new Error('Ошибка при загрузке товаров');
        }
        const data = await response.json();
        products.value = data;
      } catch (error) {
        console.error('Ошибка при загрузке товаров:', error);
      }
    };

    // Извлекаем бренды из категорий
    const extractBrands = categoriesData => {
      const allBrands = new Set();
      categoriesData.forEach(category => {
        if (category.brand) {
          allBrands.add(category.brand);
        }
      });
      brands.value = Array.from(allBrands);
    };

    // Извлекаем уникальные характеристики
    const extractCharacteristics = categoriesData => {
      const characteristicsData = {};
      categoriesData.forEach(item => {
        if (item.characteristics && Array.isArray(item.characteristics)) {
          item.characteristics.forEach(characteristic => {
            const charName = characteristic.characteristic;
            const charValue = characteristic.value;
            if (!characteristicsData[charName]) {
              characteristicsData[charName] = new Set();
            }
            characteristicsData[charName].add(charValue);
          });
        }
      });
      for (const charName in characteristicsData) {
        characteristicsData[charName] = Array.from(characteristicsData[charName]);
      }
      characteristics.value = {
        ...characteristicsData
      };
    };

    // Фильтрация товаров по выбранным брендам и характеристикам
    const filteredProducts = computed(() => {
      return products.value.filter(product => {
        const matchesBrand = !selectedBrand.value || selectedBrand.value === product.brand;
        const matchesCharacteristics = Object.keys(selectedCharacteristics.value).every(characteristic => {
          if (!selectedCharacteristics.value[characteristic]) return true;
          const productValues = product.characteristics ? product.characteristics.filter(item => item.characteristic === characteristic).map(item => item.value) : [];
          return selectedCharacteristics.value[characteristic] === productValues[0];
        });
        return matchesBrand && matchesCharacteristics;
      });
    });

    // Функция для выбора категории
    const selectCategory = category => {
      console.log('Выбрана категория:', category);
    };
    const __returned__ = {
      categories,
      products,
      brands,
      selectedBrand,
      selectedCharacteristics,
      characteristics,
      selectedBrands,
      handleBrandChange,
      loadCategories,
      loadProducts,
      extractBrands,
      extractCharacteristics,
      filteredProducts,
      selectCategory,
      ref,
      onMounted,
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
