import { computed, ref } from 'vue';

// Пропс totalProducts
const productsPerPage = 10;

// Вычисляемое свойство для общего количества страниц

export default {
  __name: 'ProductPagination',
  props: ['totalProducts'],
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const currentPage = ref(1);
    const totalPages = computed(() => Math.ceil(__props.totalProducts / productsPerPage));
    const nextPage = () => {
      if (currentPage.value < totalPages.value) currentPage.value++;
    };
    const prevPage = () => {
      if (currentPage.value > 1) currentPage.value--;
    };
    const __returned__ = {
      currentPage,
      productsPerPage,
      totalPages,
      nextPage,
      prevPage,
      computed,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
